def download_pdf(url, save_dir) -> bool:
    try:
        response = requests.get(url)
        if response.status_code == 200:
            filename = os.path.basename(url)
            filepath = os.path.join(save_dir, filename)
            with open(filepath, 'wb') as f:
                f.write(response.content)
            print(f"Downloaded: {filename}")
            return True
        else:
            print(f"Error downloading from {url}: Status code {response.status_code}")
    except Exception as e:
        print(f"Error downloading from {url}: {e}")
    return False

for centre_code in centre_codes:
    base_url = f"https://neetfs.ntaonline.in/NEET_2024_Result/{centre_code}"
    for i in range(1, 99):
        if i < 10:
            url = f"{base_url}0{i}.pdf"
        else:
            url = f"{base_url}{i}.pdf"
        if download_pdf(url, 'centre_wise_result_pdf') == False:
            break